import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AlertComponent = () => {
  const [showAlert, setShowAlert] = useState(false);

  const handleRedirecct = (url) => {
    navigate(url);
  };

  const handleClick = () => {
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 3000);  // Alert disappears after 3 seconds
  };

  return (
    <div>
      <button onClick={handleClick}>Show Alert</button>

      {showAlert && (
        <div style={alertStyles} role="alert">
          <button onClick={handleRedirecct('/account')}>Go to url</button>
        </div>
      )}
    </div>
  );
};

// Basic styles for the alert box
const alertStyles = {
  padding: '20px',
  backgroundColor: '#f44336',
  color: 'white',
  borderRadius: '5px',
  marginTop: '10px',
  textAlign: 'center',
  width: '250px',
  margin: '0 auto',
};

export default AlertComponent;
